mapper.select:
SELECT *
	FROM (SELECT ROWNUM, m.*, FLOOR(ROWNUM -1) / 9 + 1 PAGE 
		FROM ( SELECT * FROM (SELECT * from ORDER_TBL JOIN (SELECT A.order_index, SUM(B.product_count) product_count_sum
	    	FROM order_tbl A, order_product_tbl B
	    	WHERE A.order_index = SUBSTR(B.order_product_index, 1, 16)
	    	GROUP BY A.order_index ORDER BY A.order_index ASC) C ON order_tbl.ORDER_INDEX = C.order_index)
	    	ORDER BY ORDER_DATE DESC
		) m
	);	
WHERE page = #{currentPage}
